package com.shika.test;

import static org.junit.Assert.*;

import org.junit.Test;

import com.shika.security.Monoalphabetic;

public class MonoalphabeticTest {

	String mainPlain = "meetmeafterthetogaparty";
    String mainCipher = "phhwphdiwhuwkhwrjdsduwb";
    String mainKey = "defghijklmnopqrstuvwxyzabc";


    String mainPlain1 = "abcdefghijklmnopqrstuvwxyz";
    String mainCipher1 = "isyvkjruxedzqmctplofnbwgah";
    String mainKey1 = "isyvkjruxedzqmctplofnbwgah";

    String mainPlain2 = "hellosecuritymonoalphabetic";
    String mainCipher2 = "ukzzcokynlxfaqcmciztuiskfxy";
    
    String largePlain = ""
    		+""+
    		""
    		+"";
    String largeCipher=""+
    		""+
    		""+
    		""+
    		""+
    		"ROGDBVZHUHWKHKDSSHVWGDBVRIPB";
    String largeKey = "defghijklmnopqrstuvwxyzabc";

    String newPlain = "ENGLISHASTRONOMERWILLIAMLASSELLDISCOVEREDTRITON".toLowerCase();
    String newCipher = "EGSDAMTUMOLHGHFELWADDAUFDUMMEDDVAMIHQELEVOLAOHG".toLowerCase();
    String newKey = "UNIVERSTABCDFGHJKLMOPQWXYZ".toLowerCase();

    
    @Test
    public void MonoTest1()
    {
        Monoalphabetic algorithm = new Monoalphabetic();
        String cipher = algorithm.encrypt(mainPlain, mainKey);
        assertTrue(cipher.equals(mainCipher));
    }

    @Test
    public void MonoTest2()
    {
        Monoalphabetic algorithm = new Monoalphabetic();
        String plain = algorithm.decrypt(mainCipher, mainKey);
        assertTrue(plain.equals(mainPlain));
    }
    @Test
    public void MonoTest4()
    {
        Monoalphabetic algorithm = new Monoalphabetic();
        String cipher = algorithm.encrypt(mainPlain1, mainKey1);
        assertTrue(cipher.equals(mainCipher1));
    }
    
    @Test
    public void MonoTest5()
    {
        Monoalphabetic algorithm = new Monoalphabetic();
        String plain = algorithm.decrypt(mainCipher1, mainKey1);
        assertTrue(plain.equals(mainPlain1));
    }


    @Test
    public void MonoTest7()
    {
        Monoalphabetic algorithm = new Monoalphabetic();
        String cipher = algorithm.encrypt(mainPlain2, mainKey1);
        assertTrue(cipher.equals(mainCipher2));
    }
    

    @Test
    public void MonoTest8()
    {
        Monoalphabetic algorithm = new Monoalphabetic();
        String plain = algorithm.decrypt(mainCipher2, mainKey1);
        assertTrue(plain.equals(mainPlain2));
    }

    @Test
    public void MonoTest9()
    {
        Monoalphabetic algorithm = new Monoalphabetic();
        String cipher = algorithm.encrypt(largePlain, largeKey);
        assertTrue(cipher.equals(largeCipher.toLowerCase()));
    }


    @Test
    public void MonoTest10()
    {
        Monoalphabetic algorithm = new Monoalphabetic();
        String cipher = algorithm.encrypt(newPlain, newKey);
        assertTrue(cipher.equals(newCipher));
    }

    @Test
    public void MonoTest11()
    {
        Monoalphabetic algorithm = new Monoalphabetic();
        String plain = algorithm.decrypt(newCipher, newKey);
        assertTrue(plain.equals(newPlain));
    }

    
    


}
